using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Data.OleDb;\n\npublic class DatabaseMerger {\n    public static void Main() {\n        // Define connection strings\n        string accessConnectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\\path_to_access_file.mdb;";\n        string sqlServerConnectionString = "Server=myServerAddress;Database=myDataBase;User Id=myUsername;Password=myPassword;";\n\n        // Setup connections\n        using (OleDbConnection accessConnection = new OleDbConnection(accessConnectionString)) {\n            using (SqlConnection sqlServerConnection = new SqlConnection(sqlServerConnectionString)) {\n                try {\n                    // Fill data from Access\n                    var accessDataAdapter = new OleDbDataAdapter("SELECT * FROM YourAccessTable", accessConnection);\n                    DataTable accessDataTable = new DataTable();\n                    accessDataAdapter.Fill(accessDataTable);\n\n                    // Fill data from SQL Server\n                    var sqlServerDataAdapter = new SqlDataAdapter("SELECT * FROM YourSqlServerTable", sqlServerConnection);\n                    DataTable sqlServerDataTable = new DataTable();\n                    sqlServerDataAdapter.Fill(sqlServerDataTable);\n\n                    // Create DataSet and add tables\n                    DataSet dataSet = new DataSet();\n                    dataSet.Tables.Add(accessDataTable);\n                    dataSet.Tables.Add(sqlServerDataTable);\n\n                    // Define a DataRelation for combining tables based on some key\n                    DataColumn parentColumn = accessDataTable.Columns["YourKeyField"];\n                    DataColumn childColumn = sqlServerDataTable.Columns["YourForeignKeyField"];\n                    DataRelation relation = new DataRelation("relationName", parentColumn, childColumn);\n                    dataSet.Relations.Add(relation);\n\n                    // Now you can filter data across the relation if necessary\n                    foreach (DataRow row in accessDataTable.Rows) {\n                        DataRow[] linkedRows = row.GetChildRows(relation);\n                        // Process both sets of rows\n                    }\n                } catch (Exception ex) {\n                    Console.WriteLine(ex.Message);\n                }\n            }\n        }\n    }\n}