// Check if data is already in cache\nvar myData = HttpContext.Current.Cache["MyDataSet"] as DataSet;\nif (myData == null)\n{\n    // Data not in cache, retrieve it\n    myData = GetDataFromDatabase();\n    \n    // Store data in cache with an expiration policy\n    HttpContext.Current.Cache.Add("MyDataSet", myData, null, DateTime.Now.AddMinutes(10), Cache.NoSlidingExpiration, CacheItemPriority.Normal, null);\n}\n\n// Use the data