using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            using (SqlConnection connection = new SqlConnection("your_connection_string"))\n            {\n                connection.Open();\n                // Execute your command here\n            }\n        }\n        catch (SqlException ex)\n        {\n            switch (ex.Number)\n            {\n                case 2627: // Unique constraint or primary key violation\n                    Console.WriteLine("Primary Key or Unique Constraint Violation");\n                    break;\n                case 547:  // Foreign key constraint violation\n                    Console.WriteLine("Foreign Key Constraint Violation");\n                    break;\n                case 18456: // Login failed for user.\n                    Console.WriteLine("Login Failed");\n                    break;\n                default:\n                    Console.WriteLine($"An SQL error occurred. Error code: {ex.Number}");\n                    break;\n            }\n        }\n    }\n}