using System;\nusing System.Drawing;\n\npublic class ImageCropper\n{\n    public static void CropWhiteSpace(string inputFilePath, string outputFilePath)\n    {\n        using (Image image = Image.FromFile(inputFilePath))\n        {\n            Rectangle rect = GetCropRectangle((Bitmap)image, Color.White);\n            Bitmap croppedImage = new Bitmap(rect.Width, rect.Height);\n\n            using (Graphics g = Graphics.FromImage(croppedImage))\n            {\n                g.DrawImage(image, 0, 0, rect, GraphicsUnit.Pixel);\n            }\n\n            croppedImage.Save(outputFilePath);\n        }\n    }\n\n    private static Rectangle GetCropRectangle(Bitmap bmp, Color backgroundColor)\n    {\n        int topRow = -1, bottomRow = bmp.Height, leftCol = -1, rightCol = bmp.Width;\n\n        for (int y = 0; y < bmp.Height; y++)\n        {\n            for (int x = 0; x < bmp.Width; x++)\n            {\n                if (!IsColorEqual(bmp.GetPixel(x, y), backgroundColor))\n                {\n                    // Set top row\n                    if (topRow == -1) { topRow = y; }\n\n                    bottomRow = y + 1;\n                    if (leftCol > x || leftCol == -1) { leftCol = x; }\n                    if (rightCol < x + 1) { rightCol = x + 1; }\n                }\n            }\n        }\n\n        return new Rectangle(leftCol, topRow, rightCol - leftCol, bottomRow - topRow);\n    }\n\n    private static bool IsColorEqual(Color color1, Color color2)\n    {\n        return color1.R == color2.R && color1.G == color2.G && color1.B == color2.B;\n    }\n}