using System;\nusing System.Net;\nusing System.Net.Mail;\n\npublic void SendExceptionReport(string errorMessage)\n{\n    try\n    {\n        MailMessage mail = new MailMessage();\n        mail.From = new MailAddress("your-email@example.com");\n        mail.To.Add("fogbugz-email@example.com");\n        mail.Subject = "Error Report from Application";\n        mail.Body = errorMessage;\n\n        SmtpClient smtpClient = new SmtpClient("smtp.example.com")\n        {\n            Port = 587, // Typical port for TLS\n            UseDefaultCredentials = false,\n            Credentials = new NetworkCredential("your-email@example.com", "your-password"),\n            EnableSsl = true\n        };\n\n        smtpClient.Send(mail);\n    }\n    catch (Exception ex)\n    {\n        // Handle possible exceptions from Send method\n        Console.WriteLine("Failed to send email: " + ex.Message);\n    }\n}