public void UpdateColumnsVisibility(bool allTeamsVisible)\n{\n    var dataGrid = ... // reference to your DataGrid\n    var columns = dataGrid.Columns;\n\n    foreach (var column in columns)\n    {\n        if (column is DataGridTextColumn textColumn && textColumn.Header.ToString() == "YourColumnName")\n        {\n            textColumn.Visibility = allTeamsVisible ? Visibility.Visible : Visibility.Collapsed;\n        }\n        // Extend this check for other columns as needed.\n    }\n}