using System;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string architecture = GetProcessorArchitecture();\n        Console.WriteLine("The CPU Architecture is: " + architecture);\n    }\n\n    private static string GetProcessorArchitecture()\n    {\n        if (IntPtr.Size == 8)\n        {\n            return "x64"; // 64-bit programs run only on Win64\n        }\n        else if (IntPtr.Size == 4)\n        {\n            // Check if 32-bit program is running on Win32 or in WoW64.\n            if (Environment.Is64BitOperatingSystem) // .NET 4.0+ property\n            {\n                return "x86"; // It's a 32-bit process but the OS is 64-bit (WoW64).\n            }\n            else\n            {\n                return "x86";\n            }\n        }\n        throw new NotSupportedException("Unknown processor architecture.");\n    }\n}