public partial class LockScreenForm : Form\n{\n    public LockScreenForm()\n    {\n        InitializeComponent();\n        TopMost = true; // Keeps the form on top of all other windows.\n        WindowState = FormWindowState.Maximized;\n        FormBorderStyle = FormBorderStyle.None; // Fullscreen without borders.\n        KeyDown += LockScreenForm_KeyDown; // Handle key presses to prevent exiting.\n    }\n\n    private void LockScreenForm_KeyDown(object sender, KeyEventArgs e)\n    {\n        e.Handled = true; // Prevent any keyboard input.\n    }\n}