using System.Web.Http;\nusing System.Threading.Tasks;\nusing System.Collections.Concurrent;\n\npublic class FileProcessingController : ApiController\n{\n    private static readonly ConcurrentDictionary<string, int> ProgressTracker = new ConcurrentDictionary<string, int>();\n\n    [HttpPost]\n    public async Task<IHttpActionResult> StartFileOperation()\n    {\n        var taskId = Guid.NewGuid().ToString();\n        ProgressTracker[taskId] = 0;\n\n        // Start the background task\n        Task.Run(() => ProcessFiles(taskId));\n\n        return Ok(new { TaskId = taskId });\n    }\n\n    private void ProcessFiles(string taskId)\n    {\n        for (int i = 0; i <= 100; i++) // Simulate file processing progress\n        {\n            ProgressTracker[taskId] = i;\n            Task.Delay(100).Wait(); // Simulate work by delaying\n        }\n    }\n}