using System;\nusing System.Data.OleDb;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Provider=Microsoft.ACE.OLEDB.12.0;Data Source=path_to_your_database.accdb;";\n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            // SQL query to fetch all records for updating fields\n            string selectQuery = "SELECT [ID], [Salary], [SomeNumber] FROM YourTableName";\n            string updateQuery = "UPDATE YourTableName SET Salary=@newSalary, SomeNumber=@newSomeNumber WHERE ID=@id";\n\n            connection.Open();\n\n            using (OleDbDataReader reader = new OleDbCommand(selectQuery, connection).ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    double salary = Convert.ToDouble(reader["Salary"]);\n                    int someNumber = Convert.ToInt32(reader["SomeNumber"]);\n                    int id = Convert.ToInt32(reader["ID"]);\n\n                    // Apply the business rules to calculate new values\n                    double factor = 1.1; // Example factor for demonstration\n                    double newSalary = salary * factor;\n                    int newSomeNumber = GetMyBusinessRuleOn(someNumber); // Implement your logic\n\n                    using (OleDbCommand command = new OleDbCommand(updateQuery, connection))\n                    {\n                        command.Parameters.AddWithValue("@newSalary", newSalary);\n                        command.Parameters.AddWithValue("@newSomeNumber", newSomeNumber);\n                        command.Parameters.AddWithValue("@id", id);\n\n                        command.ExecuteNonQuery();\n                    }\n                }\n            }\n        }\n    }\n\n    static int GetMyBusinessRuleOn(int number)\n    {\n        // Implement your specific business logic here\n        return number + 1; // Dummy example\n    }\n}