using System;\n   using Fiddler;\n\n   class Program\n   {\n       static void Main(string[] args)\n       {\n           // Initialize FiddlerCore with default flags and startup parameters\n           FiddlerCoreApplication.Startup(8080, Flags.Default);\n           \n           // Subscribe to the BeforeRequest event to inspect or modify requests\n           FiddlerCoreApplication.BeforeRequest += delegate(Session oSession) {\n               Console.WriteLine($"Before request: {oSession.fullUrl}");\n               // You can modify the request here if needed\n           };\n           \n           // Subscribe to the BeforeResponse event to inspect or modify responses\n           FiddlerCoreApplication.BeforeResponse += delegate(Session oSession) {\n               Console.WriteLine($"Before response: {oSession.ResponseCode} - {oSession.url}");\n               // You can modify the response here if needed\n           };\n           \n           Console.WriteLine("FiddlerCore is running on port 8080. Press any key to exit...");\n           Console.ReadKey();\n           \n           // Shutdown FiddlerCore when done\n           FiddlerCoreApplication.Shutdown();\n       }\n   }