using (SqlConnection destinationConnection = new SqlConnection("YourDestinationConnectionString"))\n{\n    using (OleDbConnection sourceConnection = new OleDbConnection("YourODBCConnectionString"))\n    {\n        string selectQuery = "SELECT * FROM SourceTable";\n        OleDbCommand commandSourceData = new OleDbCommand(selectQuery, sourceConnection);\n\n        using (SqlBulkCopy bulkCopy = \n            new SqlBulkCopy(destinationConnection.ConnectionString, SqlBulkCopyOptions.Default))\n        {\n            sourceConnection.Open();\n            destinationConnection.Open();\n\n            // Set up the SQL Bulk Copy\n            bulkCopy.DestinationTableName = "SQLStagingTable";\n            \n            // Optional: Specify column mappings if necessary\n            //bulkCopy.ColumnMappings.Add("SourceColumn", "DestinationColumn");\n            \n            using (OleDbDataReader dr = commandSourceData.ExecuteReader())\n            {\n                // Write from data reader to SQL table.\n                bulkCopy.WriteToServer(dr);\n            }\n        }\n    }\n}