using System;\nusing System.Net.Sockets;\nusing System.Text;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string smtpServer = "smtp.example.com";\n        int port = 25;\n        string fromMailAddress = "your-email@example.com";\n        string toMailAddress = "recipient@example.com";\n\n        using (TcpClient client = new TcpClient(smtpServer, port))\n        using (NetworkStream networkStream = client.GetStream())\n        {\n            SendData(networkStream, $"HELO {smtpServer}\r\n");\n            SendData(networkStream, $"MAIL FROM: <{fromMailAddress}>\r\n");\n            SendData(networkStream, $"RCPT TO: <{toMailAddress}>\r\n");\n            SendData(networkStream, "DATA\r\n");\n\n            string body = "Subject: Error Report\r\n" +\n                          "\r\nThis is an error report from your application.\r\n";\n            SendData(networkStream, body);\n            SendData(networkStream, ".\r\n"); // End of data\n            SendData(networkStream, "QUIT\r\n");\n\n            networkStream.Close();\n        }\n    }\n\n    private static void SendData(NetworkStream networkStream, string str)\n    {\n        byte[] buffer = Encoding.ASCII.GetBytes(str);\n        networkStream.Write(buffer, 0, buffer.Length);\n    }\n}