using (var context = new DataContext())\n{\n    // Retrieve original entity\n    var originalEntity = context.YourEntities.Single(e => e.Id == modifiedEntity.Id);\n    \n    // Your stored procedure expects parameters of both current and original states\n    context.ExecuteCommand("exec UpdateYourEntity " +\n                            "@CurrentId, @CurrentValue1, @CurrentValue2, " +\n                            "@OriginalValue1, @OriginalValue2",\n                           modifiedEntity.Id,\n                           modifiedEntity.Value1,\n                           modifiedEntity.Value2,\n                           originalEntity.Value1,\n                           originalEntity.Value2);\n}