using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Example\n{\n    public static void Main()\n    {\n        List<KeyValuePair<string, int>> list = new List<KeyValuePair<string, int>>\n        {\n            new KeyValuePair<string, int>("banana", 2),\n            new KeyValuePair<string, int>("apple", 1),\n            new KeyValuePair<string, int>("cherry", 3)\n        };\n\n        // Sort the list by key and create a SortedList\n        SortedList<string, int> sortedList = new SortedList<string, int>(\n            list.OrderBy(kvp => kvp.Key)\n                .ToDictionary(kvp => kvp.Key, kvp => kvp.Value));\n\n        // Output the SortedList contents\n        foreach (var kvp in sortedList)\n        {\n            Console.WriteLine($"{kvp.Key}: {kvp.Value}");\n        }\n    }\n}