using System.Web.Mvc;\nusing System.Collections.Generic;\n\npublic class WebsiteController : Controller\n{\n    // GET: Website/Edit/5\n    public ActionResult Edit(int id)\n    {\n        // For demonstration, we are creating a list manually.\n        var websiteList = new WebsiteList\n        {\n            new Website { Id = 1, URL = "http://example.com", Name = "Example" },\n            new Website { Id = 2, URL = "http://test.com", Name = "Test" }\n        };\n\n        // Find the website by ID to pass to the view.\n        var website = websiteList.Find(w => w.Id == id);\n        \n        if (website == null)\n        {\n            return HttpNotFound();\n        }\n\n        return View(website);\n    }\n\n    // POST: Website/Edit/5\n    [HttpPost]\n    public ActionResult Edit(Website website)\n    {\n        if (!ModelState.IsValid)\n        {\n            return View(website);\n        }\n\n        // Here you would update your data source with the edited website information.\n        // For now, let's just pretend we updated it and redirect to a success page.\n\n        return RedirectToAction("Index");\n    }\n}