using System;\nusing System.Data;\nusing System.Xml;\n\npublic class FoodCatalogExtractor\n{\n    public void BindUniqueCatalogsToDropdown(XmlDocument xmlDoc, DropDownList dropDownList)\n    {\n        // Create a DataView from the XML data\n        DataSet dataSet = new DataSet();\n        DataTable dataTable = new DataTable("Catalogs");\n\n        XmlNodeList foodNodes = xmlDoc.SelectNodes("/data/food");\n        DataColumn uniqueColumn = new DataColumn("catlog", typeof(string));\n        dataTable.Columns.Add(uniqueColumn);\n\n        foreach (XmlNode foodNode in foodNodes)\n        {\n            int id = Convert.ToInt32(foodNode["id"].InnerText);\n            string name = foodNode["name"].InnerText;\n            string catlog = foodNode["catlog"].InnerText;\n\n            // Check if the catalog already exists\n            DataRow foundRow = dataTable.AsEnumerable()\n                                      .FirstOrDefault(row => row.Field<string>("catlog") == catlog);\n\n            // If not, add it to the DataTable\n            if (foundRow == null)\n            {\n                DataRow dataRow = dataTable.NewRow();\n                dataRow["catlog"] = catlog;\n                dataTable.Rows.Add(dataRow);\n            }\n        }\n\n        // Create DataView from DataTable\n        DataView dv = new DataView(dataTable);\n\n        // Bind DataView to DropDownList\n        dropDownList.DataSource = dv;\n        dropDownList.DataTextField = "catlog";\n        dropDownList.DataValueField = "catlog";\n        dropDownList.DataBind();\n    }\n}