using System;\nusing Microsoft.Office.Interop.Access;\n\nclass Program\n{\n    static void RelinkTables()\n    {\n        string fooDbPath = @"C:\path\to\fooDb.mdb";\n        string newBarDbPath = @"C:\path\to\newBarDb.mdb";\n\n        Application access = null;\n        try\n        {\n            // Open Access application and database\n            access = new Microsoft.Office.Interop.Access.Application();\n            access.OpenCurrentDatabase(fooDbPath);\n\n            // Iterate through TableDefs to find and re-link tables from barDb\n            foreach (TableDef table in access.CurrentDb().TableDefs)\n            {\n                if (table.Connect.Contains("barDb"))\n                {\n                    string newConnectString = $"MS Access;DATABASE={newBarDbPath};";\n                    table.Connect = newConnectString;\n                    table.RefreshLink();\n                }\n            }\n\n            // Save and close the database\n            access.DoCmd.CloseDatabase(false);\n        }\n        finally\n        {\n            if (access != null)\n            {\n                System.Runtime.InteropServices.Marshal.ReleaseComObject(access);\n            }\n        }\n    }\n\n    static void Main()\n    {\n        RelinkTables();\n    }\n}