// Construct the URL manually ensuring & is not encoded\nstring baseUrl = "https://www.parurval.se/urval/";\nstring username = parSettings.GetSettings(parSettings.SettingsType.PARSelection, parSettings.SectionType.Username);\nstring password = parSettings.GetSettings(parSettings.SettingsType.PARSelection, parSettings.SectionType.Password);\n\n// Manually format the URL to ensure proper '&' usage\nstring urlWithCredentials = $"{baseUrl}?username={HttpUtility.UrlEncode(username)}&password={HttpUtility.UrlEncode(password)}";\n\n// Set the iframe source attribute\niFrame.Attributes["src"] = urlWithCredentials;