using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Importing necessary functions from inpoutx64.dll\n    [DllImport("inpoutx64.dll", SetLastError = true)]\n    public static extern short Out32(short PortAddress, short Data);\n\n    static void Main()\n    {\n        const short PortAddress = 0x378; // Example port address, check your device's actual port\n        short dataToSend = 0xFF;         // Example data to send\n\n        try\n        {\n            Out32(PortAddress, dataToSend);\n            Console.WriteLine("Data sent successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}