// Assuming ude is your UltraDateTimeEditor control\nthis.ude.FormatString = "dd/MM/yyyy HH:mm"; // This will display both date and time\nthis.ude.MaskInput = "{LOC:" + this.ude.FormatString + "}"; // Apply the mask input accordingly\n\n// Ensure that ShowTime property is set to true if you are using ShowDateAndTime property\nthis.ude.ShowDateAndTime = DateTimeEditStyles.DateAndTime;