using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example: Stop MySQL service\n        StartStopService("mysql", false);\n\n        // Example: Start MySQL service\n        StartStopService("mysql", true);\n    }\n\n    static void StartStopService(string serviceName, bool start)\n    {\n        ProcessStartInfo pstart = new ProcessStartInfo();\n        pstart.FileName = "cmd.exe";\n        pstart.UseShellExecute = false;\n        pstart.CreateNoWindow = true;\n        pstart.RedirectStandardOutput = true;\n\n        // Determine the command based on whether to start or stop the service\n        string cmd = start ? "net start" : "net stop";\n        pstart.Arguments = $"{cmd} {serviceName}";\n\n        Process p = new Process();\n        p.StartInfo = pstart;\n        try\n        {\n            p.Start();\n            string output = p.StandardOutput.ReadToEnd();\n            Console.WriteLine(output);\n            p.WaitForExit();\n\n            if (p.ExitCode != 0)\n                Console.WriteLine($"Failed to execute command: {cmd} {serviceName}");\n            else\n                Console.WriteLine($"{(start ? "Started" : "Stopped")} service: {serviceName}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}