public static void RecordToFile(string filename, Log log)\n{\n    try\n    {\n        string path = Constants.APP_PATH + "\\" + filename;\n        using (StreamWriter textWriter = new StreamWriter(path, true))\n        {\n            textWriter.WriteLine(log.ToString());\n            textWriter.Flush(); // Ensure all data is written to the file\n        }\n    }\n    catch (Exception ex)\n    {\n        // Log exception details here if possible, or handle it appropriately\n        Console.WriteLine("Error writing log: " + ex.Message);\n    }\n}