var cfg = new Configuration();\ncfg.Configure(); // Load hibernate.cfg.xml or similar config\n\n// Specify the Oracle Data Provider for .NET dialect and driver\ncfg.SetProperty(Environment.Dialect, "NHibernate.Dialect.Oracle10gDialect");\ncfg.SetProperty(Environment.ConnectionDriver_class, "NHibernate.Driver.OracleDataClientDriver");\n\n// Build session factory and proceed with schema update\nvar sessionFactory = cfg.BuildSessionFactory();\nusing (var session = sessionFactory.OpenSession())\n{\n    new SchemaUpdate(cfg).Execute(false, true);\n}