long startTime = GetCurrentTimeInMilliseconds(); // Record start time\n\n// Make an HTTP request to your ASP.NET page\nHttpResponse response = SendHttpRequest();\n\nlong endTime = GetCurrentTimeInMilliseconds(); // Record end time\nint responseDataSize = GetResponseDataSize(response); // Size of the data returned by the server in bytes\n\n// Calculate duration in seconds (time taken for the round trip)\ndouble durationInSeconds = (endTime - startTime) / 1000.0;\n\nif (durationInSeconds > 0) {\n    // Calculate bandwidth in bytes per second\n    double bandwidthInBytesPerSecond = responseDataSize / durationInSeconds;\n    \n    // Log or store the bandwidth for graphing later\n}