using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass MyClass\n{\n    public int Value { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        List<MyClass> list = new List<MyClass>\n        {\n            new MyClass { Value = 10 },\n            new MyClass { Value = 5 },\n            new MyClass { Value = 20 }\n        };\n\n        MyClass minObj = list.OrderBy(x => x.Value).First();\n        MyClass maxObj = list.OrderByDescending(x => x.Value).First();\n\n        Console.WriteLine("Min: " + minObj.Value);\n        Console.WriteLine("Max: " + maxObj.Value);\n    }\n}