using System;\nusing System.Data;\n\npublic void FormatDateColumn(DataSet dataSet)\n{\n    // Assuming the DataTable is the first table in the DataSet and the column index or name is known.\n    DataTable dataTable = dataSet.Tables[0];\n    string dateColumnName = "YourDateColumnName"; // Replace with your actual column name\n\n    foreach (DataRow row in dataTable.Rows)\n    {\n        if (!Convert.IsDBNull(row[dateColumnName]))\n        {\n            DateTime dateTimeValue = Convert.ToDateTime(row[dateColumnName]);\n            row[dateColumnName] = dateTimeValue.ToString("d/M/yyyy");\n        }\n    }\n}