using System;\nusing HtmlAgilityPack;\n\npublic class HtmlProcessor\n{\n    public string Processing(string Source, string Tag, string Attribute)\n    {\n        var doc = new HtmlDocument();\n        doc.LoadHtml(Source);\n\n        foreach (var node in doc.DocumentNode.SelectNodes($"//{Tag}[@{Attribute}]"))\n        {\n            node.Attributes.Remove(Attribute);\n        }\n\n        return doc.DocumentNode.OuterHtml;\n    }\n\n    public static void Main()\n    {\n        string before = @"<input type=""text"" name=""Input"" id=""Input"" onMouseOver=""some js to be eliminated"">";\n        HtmlProcessor processor = new HtmlProcessor();\n        string after = processor.Processing(before, "input", "onMouseOver");\n        Console.WriteLine(after); // expected output: <input type="text" name="Input" id="Input">\n    }\n}