using MicroLite;\nusing System.Data.SqlServerCe;\n\npublic class Setup\n{\n    public static void Configure()\n    {\n        // Initialize MicroLite with the connection string and dialect specific to SQL CE\n        var configuration = new Configuration();\n        configuration.AddConnection("default", "Data Source=|DataDirectory|\YourDatabase.sdf;", SqlServerCeDialect.ProviderName);\n        \n        SessionFactory.Configure(configuration);\n    }\n}\n\n// Example usage:\npublic class YourContext\n{\n    public void InsertEntity(YourEntity entity)\n    {\n        using (ISession session = SessionFactory.OpenSession())\n        {\n            session.Insert(entity);\n        }\n    }\n}