ManagementObjectSearcher searcher = new ManagementObjectSearcher("select * from Win32_LogicalDisk WHERE DriveType=5");\nforeach (ManagementObject mo in searcher.Get())\n{\n    if (mo["FreeSpace"] != null && mo["Size"] != null)\n    {\n        Console.WriteLine("Drive: " + mo["Name"].ToString());\n        Console.WriteLine("FreeSpace: " + (Convert.ToUInt64(mo["FreeSpace"]) / 1024 / 1024) + " MB");\n        Console.WriteLine("CapacitySpace: " + (Convert.ToUInt64(mo["Size"]) / 1024 / 1024) + " MB");\n        UInt64 usedspace = (UInt64)mo["Size"] - (UInt64)mo["FreeSpace"];\n        Console.WriteLine("UsedSpace: " + (usedspace / 1024 / 1024) + " MB");\n    }\n    else\n    {\n        // Handle situation where FreeSpace or Size is unavailable, likely a CDFS drive.\n        Console.WriteLine("Drive: " + mo["Name"].ToString() + " reports no free space information.");\n    }\n}