[WebInvoke(Method = "POST")]\npublic PayDetails CalculatePay(PayRequest request)\n{\n    // Logic to calculate benefits and deductions using request parameters\n    var payDetails = new PayDetails\n    {\n        EmployeeId = request.EmployeeId,\n        Benefits = CalculateBenefits(request.GrossPay),\n        Deductions = CalculateDeductions(request.GrossPay)\n    };\n\n    return payDetails;\n}\n\npublic class PayRequest\n{\n    public int EmployeeId { get; set; }\n    public decimal GrossPay { get; set; }\n}