using System;\nusing System.IO;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        string certificatePath = "path_to_your_certificate.pfx";\n        string password = "your_password";\n        byte[] signedData = File.ReadAllBytes("path_to_your_file");\n\n        // Load the X.509 certificate with private key.\n        var certificate = new X509Certificate2(certificatePath, password);\n\n        using (var rsa = certificate.GetRSAPrivateKey())\n        {\n            if (rsa == null)\n            {\n                throw new InvalidOperationException("Cannot extract RSA private key.");\n            }\n\n            byte[] signature = rsa.SignData(signedData, HashAlgorithmName.SHA256, RSASignaturePadding.Pkcs1);\n            \n            // Now you can save the signature or apply it to your document.\n        }\n    }\n}