using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    SqlCommand command1 = new SqlCommand("SELECT CustomerID, CustomerEmail, CustomerAddress FROM Customers WHERE CustomerID=@CustomerID", connection);\n    SqlCommand command2 = new SqlCommand("SELECT InventoryLineItem, ShipQty FROM Orders WHERE CustomerID=@CustomerID", connection);\n\n    command1.Parameters.AddWithValue("@CustomerID", 55);\n    command2.Parameters.AddWithValue("@CustomerID", 55);\n\n    connection.Open();\n\n    // Execute customer details query\n    SqlDataReader reader1 = command1.ExecuteReader();\n    if (reader1.Read())\n    {\n        int customerId = reader1.GetInt32(0);\n        string email = reader1.GetString(1);\n        string address = reader1.GetString(2);\n        \n        // Use the data as needed...\n        Console.WriteLine($"Customer: {customerId}, Email: {email}, Address: {address}");\n    }\n    reader1.Close();\n\n    // Execute order details query\n    SqlDataReader reader2 = command2.ExecuteReader();\n    while (reader2.Read())\n    {\n        string inventoryLineItem = reader2.GetString(0);\n        int shipQty = reader2.GetInt32(1);\n        \n        // Use the data as needed...\n        Console.WriteLine($"Inventory Line Item: {inventoryLineItem}, Quantity: {shipQty}");\n    }\n    reader2.Close();\n}