using iTextSharp.text.pdf;\n\n// Assuming 'stamper' is your PdfStamper object and "fieldName" is the name of your field\nPdfReader reader = stamper.Reader;\nAcroFields af = stamper.AcroFields;\nstring fieldName = "fieldName";\n\n// Get the widget annotations for the field\nArrayList annots = af.GetFieldItemList(fieldName).GetAll(null);\nforeach (DictionaryEntry entry in annots)\n{\n    PdfDictionary widgetDict = entry.Key as PdfDictionary;\n    if (widgetDict != null)\n    {\n        // Check if there is a /Rotate key and get its value\n        PdfNumber rotateAngle = widgetDict.GetAsNumber(PdfName.ROTATE);\n        if (rotateAngle != null)\n        {\n            int rotationDegrees = rotateAngle.IntValue;\n            Console.WriteLine($"The field '{fieldName}' is rotated by {rotationDegrees} degrees.");\n        }\n        else\n        {\n            Console.WriteLine($"The field '{fieldName}' is not rotated.");\n        }\n    }\n}