using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        string[] inputArray = { "a", "a", "b", "c", "c", "a", "c", "d", "c", "d" };\n        List<string> reducedList = ReduceSequences(inputArray);\n\n        Console.WriteLine(string.Join(", ", reducedList));\n    }\n\n    static List<string> ReduceSequences(string[] input)\n    {\n        if (input == null || input.Length == 0)\n            return new List<string>();\n\n        List<string> result = new List<string>();\n        string lastElement = null;\n\n        foreach (string current in input)\n        {\n            if (current != lastElement)\n            {\n                result.Add(current);\n                lastElement = current;\n            }\n        }\n\n        return result;\n    }\n}