using System;\nusing System.Collections.Generic;\nusing System.Dynamic;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create an instance of ExpandoObject\n        dynamic formInstance = new ExpandoObject();\n\n        // Define the properties based on your form fields dynamically\n        AddProperty(formInstance, "FirstName", "John");\n        AddProperty(formInstance, "LastName", "Doe");\n        AddProperty(formInstance, "Age", 30);\n\n        // Set and get property values\n        Console.WriteLine($"{formInstance.FirstName} {formInstance.LastName}, Age: {formInstance.Age}");\n\n        // Modify a property value\n        formInstance.Age = 31;\n        Console.WriteLine($"Updated Age: {formInstance.Age}");\n    }\n\n    static void AddProperty(ExpandoObject expando, string propertyName, object propertyValue)\n    {\n        var dictionary = expando as IDictionary<string, object>;\n        if (dictionary != null && !dictionary.ContainsKey(propertyName))\n        {\n            dictionary[propertyName] = propertyValue;\n        }\n    }\n}