using Newtonsoft.Json;\n\nclass Program\n{\n    static void Main()\n    {\n        dynamic formInstance = new ExpandoObject();\n        AddProperty(formInstance, "FirstName", "John");\n        AddProperty(formInstance, "LastName", "Doe");\n        AddProperty(formInstance, "Age", 30);\n\n        // Serialize to JSON\n        string json = JsonConvert.SerializeObject(formInstance);\n        Console.WriteLine(json);\n\n        // Deserialize back to dynamic ExpandoObject\n        dynamic deserializedFormInstance = JsonConvert.DeserializeObject<ExpandoObject>(json);\n        Console.WriteLine($"{deserializedFormInstance.FirstName} {deserializedFormInstance.LastName}, Age: {deserializedFormInstance.Age}");\n    }\n\n    static void AddProperty(ExpandoObject expando, string propertyName, object propertyValue)\n    {\n        var dictionary = expando as IDictionary<string, object>;\n        if (dictionary != null && !dictionary.ContainsKey(propertyName))\n        {\n            dictionary[propertyName] = propertyValue;\n        }\n    }\n}