using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        // Sample XHTML content as a string\n        string xhtmlContent = @"\n            <html>\n                <head><title>Sample</title></head>\n                <body>\n                    <div class='example'>First example</div>\n                    <span class='example highlight'>Second example</span>\n                    <p>No match here</p>\n                </body>\n            </html>";\n\n        // Load the XHTML content into an XmlDocument\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xhtmlContent);\n\n        // XPath expression to select nodes with a specific class name.\n        // This checks if 'example' is either the only class or one of several in space-separated list.\n        string xpathExpression = "//*[@class='example' or contains(concat(' ', normalize-space(@class), ' '), ' example ')]";\n\n        // Select all matching nodes\n        XmlNodeList nodeList = doc.SelectNodes(xpathExpression);\n\n        // Output the selected nodes\n        foreach (XmlNode node in nodeList)\n        {\n            Console.WriteLine(node.OuterXml);\n        }\n    }\n}