using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlString = @"<VAR NAME=""sf_name"" ID=""1001210"">\n<VAR NAME=""another_name"" ID=""1001211"">";\n\n        // Regex pattern to find the ID attribute and capture its value\n        string pattern = @"(ID="""")(\d+)(\""")";\n        MatchEvaluator evaluator = match =>\n        {\n            int idValue = int.Parse(match.Groups[2].Value);\n            // Modify the ID as needed, e.g., increment it by 100\n            int newIdValue = IncrementId(idValue);\n            return $"{match.Groups[1]}{newIdValue}{match.Groups[3]}";\n        };\n\n        string updatedXmlString = Regex.Replace(xmlString, pattern, evaluator);\n\n        Console.WriteLine(updatedXmlString);\n    }\n\n    static int IncrementId(int id)\n    {\n        // Define your logic here. This example increments the ID by 100\n        return id + 100;\n    }\n}