using System;\nusing System.Net.Sockets;\nusing System.Security.Cryptography.X509Certificates;\nusing System.Net.Security;\n\npublic class SslClient\n{\n    public static void Main()\n    {\n        TcpClient client = new TcpClient("serverAddress", 443);\n        using (NetworkStream stream = client.GetStream())\n        using (SslStream sslStream = new SslStream(stream, false, ValidateServerCertificate))\n        {\n            sslStream.AuthenticateAsClient("expectedHostName");\n            Console.WriteLine("Authenticated and ready to send data...");\n            \n            // Handle remoting data here\n        }\n    }\n\n    private static bool ValidateServerCertificate(object sender, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)\n    {\n        if (sslPolicyErrors == SslPolicyErrors.None)\n            return true;\n\n        Console.WriteLine("Certificate error: {0}", sslPolicyErrors);\n        return false;\n    }\n}