// Within Startup.cs or Startup.Auth.cs\npublic void ConfigureAuth(IAppBuilder app)\n{\n    // Configure the db context, user manager and sign-in manager to use a single instance per request\n    app.CreatePerOwinContext(ApplicationDbContext.Create);\n    app.CreatePerOwinContext<ApplicationUserManager>(ApplicationUserManager.Create);\n    app.CreatePerOwinContext<ApplicationSignInManager>(ApplicationSignInManager.Create);\n\n    // Enable the application to use a cookie to store information for the signed in user\n    // and to use a cookie to temporarily store information about a user logging in with a third party login provider\n    app.UseCookieAuthentication(new CookieAuthenticationOptions()\n    {\n        AuthenticationType = DefaultAuthenticationTypes.ApplicationCookie,\n        LoginPath = new PathString("/Account/Login")\n    });\n    app.UseExternalSignInCookie(DefaultAuthenticationTypes.ExternalCookie);\n\n    // Enables the application to store temporary data about a user while they are verifying the second factor in the two-factor authentication process.\n    app.UseTwoFactorSignInCookie(DefaultAuthenticationTypes.TwoFactorCookie, TimeSpan.FromMinutes(5));\n\n    // Enables the application to remember the second login verification factor such as phone or email.\n    // Once you check this option, your second step of verification during the login process will be remembered on the device where you logged in from.\n    // This is similar to the RememberMe option when you log in.\n    app.UseTwoFactorRememberBrowserCookie(DefaultAuthenticationTypes.TwoFactorRememberBrowserCookie);\n\n    // Uncomment the following lines to enable logging in with third party login providers\n    //app.UseMicrosoftAccountAuthentication(\n    //    clientId: "",\n    //    clientSecret: "");\n\n    //app.UseTwitterAuthentication(\n    //   consumerKey: "",\n    //   consumerSecret: "");\n\n    //app.UseFacebookAuthentication(\n    //   appId: "",\n    //   appSecret: "");\n\n    //app.UseGoogleAuthentication(new GoogleOAuth2AuthenticationOptions()\n    //{\n    //    ClientId = "",\n    //    ClientSecret = ""\n    //});\n}