using System;\nusing Microsoft.Office.Interop.Word;\n\nclass Program\n{\n    static void Main()\n    {\n        Application wordApp = new Application();\n        Document doc = null;\n        \n        try\n        {\n            doc = wordApp.Documents.Open(@"C:\Path\To\Template.docx");\n\n            // Replace placeholder with actual data\n            string placeholder = "[Placeholder]";\n            string replacement = "Actual Value";\n            object matchCase = true;\n            object matchWholeWord = true;\n            object matchWildCards = false;\n            object matchSoundsLike = false;\n            object matchAllWordForms = false;\n            object forward = true;\n            object wrap = WdFindWrap.wdFindContinue;\n            object format = false;\n            \n            (doc.Content.Find as Find).Execute(\n                ref placeholder, ref matchCase, ref matchWholeWord, \n                ref matchWildCards, ref matchSoundsLike, ref matchAllWordForms,\n                ref forward, ref wrap, ref format, ref replacement, \n                ref replace: 2); // wdReplaceAll\n\n            object outputFileName = @"C:\Path\To\Output.pcl";\n            object range = Microsoft.Office.Interop.Word.WdExportRange.wdExportFromTo;\n            object fromPage = Type.Missing;\n            object toPage = Type.Missing;\n            object item = WdPrintOutItem.wdPrintDocumentContent;\n            object copies = 1;\n            object pages = "";\n            object pageType = WdPrintOutPages.wdPrintAllPages;\n            object printToFile = true; // Redirect output to file\n            object collate = false;\n            object activePrinterMacGX = "HP Universal PCL Driver on Ne0:";\n            object manualDuplexPrint = false;\n            wordApp.ActivePrinter = (string)activePrinterMacGX;\n\n            doc.PrintOut(ref background, ref append,\n                         ref range, ref outputFileName, ref fromPage, ref toPage,\n                         ref item, ref copies, ref pages, ref pageType,\n                         ref printToFile, ref collate, ref activePrinterMacGX,\n                         ref manualDuplexPrint);\n\n            Console.WriteLine("PCL file created successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n        finally\n        {\n            if (doc != null)\n                doc.Close();\n            wordApp.Quit();\n        }\n    }\n}