using System;\nusing Yaml; // Make sure this namespace matches your parser's\n\npublic class Program\n{\n    public static void Main()\n    {\n        string yamlContent = @"\n- id: tagid\n  tag:\n  - name: tagname\n    value: tagvalue";\n\n        var parser = new YamlParser();\n        var doc = parser.Parse(yamlContent);\n\n        // Assuming doc.Root is a collection of mappings (sequences)\n        foreach (var mapping in doc.Root as MappingNode[])\n        {\n            // Accessing 'id' directly if it's on the same level\n            var idNode = mapping.Children[new ScalarNode("id")];\n            Console.WriteLine($"ID: {((ScalarNode)idNode).Value}");\n\n            // Accessing nested 'tag' sequence\n            var tagSequence = (SequenceNode)mapping.Children[new ScalarNode("tag")];\n\n            foreach (var tagMapping in tagSequence.Children)\n            {\n                // Accessing 'name' within each nested mapping\n                var nameNode = ((MappingNode)tagMapping).Children[new ScalarNode("name")];\n                Console.WriteLine($"Name: {((ScalarNode)nameNode).Value}");\n\n                // Similarly, you can access 'value'\n                var valueNode = ((MappingNode)tagMapping).Children[new ScalarNode("value")];\n                Console.WriteLine($"Value: {((ScalarNode)valueNode).Value}");\n            }\n        }\n    }\n}