using System;\nusing System.Collections.Generic;\nusing System.Web.UI.WebControls;\n\npublic class MultiRangeValidator : BaseCompareValidator\n{\n    public List<Tuple<int, int>> Ranges { get; set; }\n\n    protected override bool EvaluateIsValid()\n    {\n        if (Ranges == null || Ranges.Count == 0)\n        {\n            throw new InvalidOperationException("Ranges must be defined.");\n        }\n\n        var value = GetControlValidationValue(ControlToValidate);\n        int number;\n        \n        if (!int.TryParse(value, out number))\n        {\n            return false; // Not a valid integer\n        }\n\n        foreach (var range in Ranges)\n        {\n            if (number >= range.Item1 && number <= range.Item2)\n            {\n                return true; // Valid within one of the defined ranges\n            }\n        }\n\n        return false; // Invalid outside all ranges\n    }\n}