using System;\nusing HtmlAgilityPack;\n\nclass Program\n{\n    static void Main()\n    {\n        string html = @"<html>\n  <head>\n    <title>Bar</title>\n  </head>\n  <body>\n   <p>\n     this content my be found\n   </p>\n  </body>\n</html>";\n\n        HtmlDocument document = new HtmlDocument();\n        document.LoadHtml(html);\n\n        var node = document.DocumentNode.SelectSingleNode("//p[contains(., 'this content my be found')]");\n        \n        if (node != null)\n        {\n            Console.WriteLine("Text found.");\n        }\n        else\n        {\n            Console.WriteLine("Text not found.");\n        }\n    }\n}