// Example of using Log4Net in a .NET project\nusing log4net;\nusing log4net.Config;\n\npublic class LoggingExample\n{\n    private static readonly ILog Logger = LogManager.GetLogger(typeof(LoggingExample));\n\n    public LoggingExample()\n    {\n        XmlConfigurator.Configure(); // Configure Log4Net based on log4net.config settings\n    }\n\n    public void LogInfo(string message)\n    {\n        Logger.Info(message);\n    }\n}