using System;\nusing System.Reflection;\n\npublic class BuildModeChecker\n{\n    public static void CheckBuildMode(string assemblyPath)\n    {\n        AssemblyName assemblyName = new AssemblyName(assemblyPath);\n        Assembly assembly = Assembly.Load(assemblyName);\n\n        foreach (var attribute in assembly.GetCustomAttributes(false))\n        {\n            if (attribute is System.Diagnostics.DebuggableAttribute debuggableAttribute)\n            {\n                Console.WriteLine("Debug mode: " +\n                                  (debuggableAttribute.IsJITOptimizerDisabled && !debuggableAttribute.IsJITTrackingEnabled\n                                      ? "Debug"\n                                      : "Release"));\n                return;\n            }\n        }\n\n        Console.WriteLine("Could not determine build mode.");\n    }\n\n    public static void Main(string[] args)\n    {\n        if (args.Length != 1)\n        {\n            Console.WriteLine("Usage: BuildModeChecker.exe [assemblyPath]");\n            return;\n        }\n\n        CheckBuildMode(args[0]);\n    }\n}