using System;\nusing System.Data.SqlClient;\n\npublic class ConnectionPoolChecker {\n    public static void CheckConnectionPool() {\n        string connectionString = "your_connection_string_here";\n        \n        using (SqlConnection connection = new SqlConnection(connectionString)) {\n            try {\n                connection.Open();\n                Console.WriteLine($"State: {connection.State}");\n                // Check current pool usage\n                var stats = new SqlConnectionStringBuilder(connection.ConnectionString);\n                Console.WriteLine($"Current Pooling: {stats.Pooling}, Min Pool Size: {stats.MinPoolSize}, Max Pool Size: {stats.MaxPoolSize}");\n\n            } catch (Exception e) {\n                Console.Error.WriteLine($"Error connecting to the database: {e.Message}");\n            }\n        }\n    }\n}