// Example of a Web API Controller method that could be used to authenticate a user.\n[HttpPost]\npublic IHttpActionResult AuthenticateUser(LoginModel model)\n{\n    if (ModelState.IsValid)\n    {\n        // Call your existing membership provider logic here\n        bool isAuthenticated = Membership.ValidateUser(model.Username, model.Password);\n        \n        if (isAuthenticated)\n        {\n            return Ok(new { Authenticated = true });\n        }\n    }\n    return Unauthorized();\n}