using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "User Id=your_user;Password=your_password;Data Source=your_data_source";\n        \n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            try\n            {\n                connection.Open();\n                Console.WriteLine("Successfully connected to the database!");\n\n                OracleCommand command = new OracleCommand("SELECT * FROM employee", connection);\n                OracleDataReader reader = command.ExecuteReader();\n\n                while (reader.Read())\n                {\n                    Console.WriteLine(reader["employee_id"] + " \t " + reader["first_name"]);\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}